<?phpDoo::loadController('I18nController');Doo::loadController('RecaptController');Doo::loadHelper('class.phpmailer');class DatosMailController extends DooController {	public function datos(){		$mail = new PHPMailer(false); 		$mail->IsSMTP(); 		$mail->Host       = "smtp.outlook365.com";		$mail->Port       = 587;		$mail->SMTPSecure = "tls";		$mail->SMTPAuth   = true; 	  //$correo_emisor="websales@supertours.com";		$correo_emisor="arturo@supertours.com";		$nombre_emisor="SuperTours"; 		$contrasena="Arturo123";	  //$mail->SMTPDebug  = 2;	  //$mail->SMTPSecure = "tsl"; 		                  		$mail->Username   = $correo_emisor; 		$mail->Password   = $contrasena;          		//$mail->AddReplyTo($correo_emisor, $nombre_emisor);		$mail->SetFrom("no_reply@supertours.com", $nombre_emisor);		$mail->AddAddress("reservations@supertours.com", "SuperTours");		$mail->IsHTML(true);				$mail->Subject = 'Reservations Super Tours';		$mail->AltBody = 'Reservations Super Tours';				return $mail;	}}?>