probarScreen();    });                $('#grid tr').click(function() {    $(this).find('input[name="item"]').prop('checked', true)    });    $('texto').keypress(function(e){       if (e.keyCode==13)           $('#form1').submit();    })         $('#btn-find').click(function(){       $('#form1').submit();     });    $('#btn-edit').click(function(e){        var id = $('input[name=item]:checked').attr('value');        if (!id){         alert('You must select an Item');         e.preventDefault();       }       else {           var action = $(this).attr("href") + "/" + id;           $(this).attr("href",action);       }   });      $('#btn-delete').click(function(e){        n = $('input[name=item]:checked').attr('value');        if (!n){          alert('You must select an Item');          e.preventDefault();        }else {            if (confirm("Are you sure of deleting this item? ..")){               var action = $(this).attr("href") + "/?item=" + n;               $(this).attr("href",action);            }else			{				return false;			}        }    });</script><script type="text/javascript">    function comprobarScreen()    {        if (window.screen.availWidth <= 640) {            window.parent.document.body.style.zoom = "62%";        }        if (window.screen.availWidth == 800) {            window.parent.document.body.style.zoom = "78%";        }        if (window.screen.availWidth == 1024) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth <= 1280) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth == 1366) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth > 1366) {            window.parent.document.body.style.zoom = "125%";        }    }</script>