m1.trip_from.selectedIndex == 0) {                        sErrMsg += "- debe seleccionar Partida \n";                    }                    if (document.form1.trip_to.selectedIndex == 0) {                        sErrMsg += "- debe seleccionar Llegada \n";                    }                    if (document.form1.trip_no.selectedIndex == 0) {                        sErrMsg += "- debe seleccionar Number Trip \n";                    }                    if (sErrMsg != "")                    {                        alert(sErrMsg);                        flag = false;                    }                    return flag;                }                $(function () {                    $.datepicker.setDefaults($.datepicker.regional["es"]);                    $("#fecha_ini").datepicker({                        firstDay: 1                    });                });                $(function () {                    $.datepicker.setDefaults($.datepicker.regional["es"]);                    $("#fecha_fin").datepicker({                        firstDay: 1                    });                });                $('#btn-save').click(function () {                    if (validateForm()) {                        //validar();                        $('#form1').submit();                    }                })                $('#btn-cancel').click(function () {                    window.location = '<?php echo $data['rootUrl']; ?>admin/routes';                })</script>