: true,                buttons: {                    Ok: function() {                        $( this ).dialog( "close" );                    }                }            });        }    });    function validateForm(){        var sErrMsg = "";        var flag = true;        /* sErrMsg += validateText($('#username').val(), $('#l_trip_no').html(), true);*/        if(sErrMsg != "")        {            alert(sErrMsg);            flag = false;        }        return flag;    }    $('#btn-save').click(function(){        if (validateForm()){            $('#form1').submit();        }    });    $('#btn-cancel').click(function(){        window.location = '<?php echo $data['rootUrl']; ?>admin/tours/hotel';    });    $(document).ready(function(){        $('#categoria').change(function(){            var cat = $(this).val();            if(cat != ''){                cat = parseInt(cat);                if(cat > 2){                    $('#id_super_breakfast').val(1);                }else{                    $('#id_super_breakfast').val(0);                }            }        });    });</script>