      firstDay: 1        });    });    $(function () {        $.datepicker.setDefaults($.datepicker.regional["es"]);        $("#fecha_fin").datepicker({            firstDay: 1        });    });    	$("#trip_no").change( function(){			var trip = $(this).val();				$("#trip_from").load('<?php echo $data['rootUrl']; ?>admin/routes/trip_from/' + trip);		}	);		$("#trip_from").change( function(){			var trip = $("#trip_no").val();				var from = $(this).val();			$("#trip_to").load('<?php echo $data['rootUrl']; ?>admin/routes/trip_to/' + trip + '/' + from);		}	);			  $(function(){              $('#acompany').removeAttr("disabled");             $('#acompany').attr('placeholder','Insert Agency');        });   //auto-complete       $("#acompany").keyup(function(){      $("#acompany").autocomplete({          source: '<?php echo $data["rootUrl"]; ?>admin/tours/loadcompany/'+$("#acompany").val(),          select: function(event, ui) {                $('#id_agency').val(ui.item.id);        }      });      });         function validateForm(){        var sErrMsg = "";        var flag = true;				if($('#id_agency').val() == -1){			 sErrMsg += "- Select the Agency.\n";		}				if($('#trip_no').val() == 0){			 sErrMsg += '- Select the Trip.\n';		}				if($('#trip_from').val() == 0){			sErrMsg += '- Select the Departure.\n';		}				if($('#trip_to').val() == 0 && $('#trip_from').val() != "*"){			sErrMsg += '- Select the Arrival.\n';		}						        sErrMsg += validateNumber($('#price').val(), $('#l_price').html(), true);        sErrMsg += validateNumber($('#price2').val(),$('#l_price2').html() , true);		sErrMsg += validateNumber($('#anno').val(),$('#l_anno').html() , true);		if($('#trip_from').val() != "*"){		sErrMsg += validateTime($('#trip_departure').val(),$('#l_trip_departure').html() , true);				sErrMsg += validateTime($('#trip_arrival').val(),$('#l_trip_arrival').html() , true);		}				        if(sErrMsg != "")        {            alert(sErrMsg);            flag = false;        }        return flag;    }    $('#btn-save').click(function(){        if (validateForm()){            $('#form1').submit();        }    })        $('#btn-cancel').click(function(){        window.location = '<?php echo $data['rootUrl']; ?>admin/home';    })    </script>