ass="input">                    <label style="width:150px" class="required" id="l_id_grupo">Category: </label>                    <select name="id_grupo" id="id_grupo" class="select">                        <?php                        if($data['dato'] == 'edit'){                            foreach($data["grupos"] as $e){                                if($e['id']==$rates_Group->id_grupo){                                    ?>                                    <option value="<?php echo  $e['id']; ?>" selected="selected"><?php echo $e["nombre"]; ?></option>                                <?php                                }                            }                        }else{                            ?>                            <option value=""></option>                            <?php foreach($data["grupos"] as $e){?>                                <option value="<?php echo  $e['id']; ?>"  <?php echo ($rates_Group->id_grupo == trim($e['id'])?'selected':''); ?>><?php echo $e["nombre"]; ?></option>                            <?php };                        }                        ?>                    </select>                </div>                <div class="input">                    <label style="width:150px" class="required" id="l_adult">Price Adults: </label>                    <input type="text" name="adult" id="adult"  size="10" maxlength="10"  value="<?php echo $rates_Group->adult; ?>" autocomplete="off"/>                </div>                <div class="input">                    <label style="width:150px" class="required" id="l_child">Price Child: </label>                    <input type="text" name="child" id="child"  size="10" maxlength="10"  value="<?php echo $rates_Group->child; ?>" autocomplete="off"/>                </div>                <div class="input">                    <label style="width:150px" class="required" for="type_rate">Type Rates</label>                 