otUrl"]; ?>admin/tours/loadcompany/'+$("#company_name").val(),            select: function(event, ui) {                $('#id_agency').val(ui.item.id);            }        });    });    function validateForm(){        var sErrMsg = "";        var flag = true;        if($('#id_grupo').val() == ""){            sErrMsg += "- Select the Category.\n";        }//        sErrMsg += validateNumberPositivo($('#adult').val(),$('#l_adult').html() , true);////////        sErrMsg += validateNumberPositivo($('#child').val(),$('#l_child').html() , true);        <?php if($data['type_rate']==2){        ?>        if($('#id_agency').val() == -1){            sErrMsg += "- Select the Agency.\n";        }        <?php       }?>        if(sErrMsg != "")        {            alert(sErrMsg);            flag = false;        }        return flag;    }    $('#btn-save').click(function(){        if (validateForm()){            $('#form1').submit();        }    })    $('#btn-cancel').click(function(){        window.location = '<?php echo $data['rootUrl']; ?>admin/tours/rates-group';    })</script>