se;        }        return flag;    }    $('#btn-save').click(function(){        if (validateForm()){            $('#form1').submit();        }    })    $('#btn-cancel').click(function(){        window.location = '<?php echo $data['rootUrl']; ?>admin/tours/tarifa-trip/<?php echo $tarifastrip->type_rate; ?>';    })</script>