elete-icon').title = 'Activate';			document.getElementById('btn-delete-txt').innerHTML = 'Activate';	   }	});		function estado(id){	   var nom = 'est_'+id;	   var estado = document.getElementById(nom).value;	   if(estado==1){		  document.getElementById('btn-delete-icon').className = 'icon-delete';		  document.getElementById('btn-delete-icon').title = 'Block';		  document.getElementById('btn-delete-txt').innerHTML = 'Block';	   }else{		    document.getElementById('btn-delete-icon').className = 'icon-active';		    document.getElementById('btn-delete-icon').title = 'Activate';			document.getElementById('btn-delete-txt').innerHTML = 'Activate';	   }	}      $('#btn-delete').click(function(e){        n = $('input[name=item]:checked').attr('value');        if (!n){          alert('You must select an item');          e.preventDefault();        }else {            if (confirm("This sure block this record ..")){			    var nom = 'est_'+n;			   var estado = document.getElementById(nom).value;               var action = $(this).attr("href") + "/?item=" + n + "/&estado=" +estado;               $(this).attr("href",action);            }else			{				return false;			}        }    });</script><script type="text/javascript">    function comprobarScreen()    {        if (window.screen.availWidth <= 640) {            window.parent.document.body.style.zoom = "62%";        }        if (window.screen.availWidth == 800) {            window.parent.document.body.style.zoom = "78%";        }        if (window.screen.availWidth == 1024) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth == 1280) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth == 1366) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth > 1366) {            window.parent.document.body.style.zoom = "125%";        }    }</script>