-dd',        minDate: 0,        numberOfMonths: 2            });        $('#fecha_fin').datepicker({        dateFormat: 'yy-mm-dd',        minDate: 0,        numberOfMonths: 2    });        $("#fecha_ini").change(function () {            var fecha_salida = $('#fecha_ini').val();            $("#fecha_fin").val(fecha_salida);                       $("#fecha_ini").datepicker("hide");            if (!Validar(fecha_salida)) {                $('#fecha_fin').focus();            } else {                var fecha_retorno = $('#fecha_fin').val();            }    });    $("#fecha_fin").change(function () {        var fecha_retorno = $('#fecha_fin').val();        $("#fecha_fin").val(fecha_retorno);        if (!Validar(fecha_retorno)) {            $('#fecha_fin').focus();        } else {            var fecha_salida = $('#fecha_ini').val();        }    });</script><script>    function combo()    {        $(document).ready(function () {            // Así accedemos al Texto de la opción seleccionada            var valor = $("#special_price_name option:selected").html();            //alert(valor);            acompany.value = valor;        });    }</script><script type="text/javascript">        $(window).load(function () {                                            comprobarScreen();                    var unlock = "<?php echo $data['type_rate']?>";                    if(unlock == 0){                            document.getElementById('unlock').style.display = 'none';          }else{              document.getElementById('unlock').style.display = '';          }                                     });</script><script type="text/javascript">    function comprobarScreen()    {        if (window.screen.availWidth <= 640) {            window.parent.document.body.style.zoom = "62%";        }        if (window.screen.availWidth == 800) {            window.parent.document.body.style.zoom = "78%";        }        if (window.screen.availWidth == 1024) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth <= 1280) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth == 1366) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth > 1366) {            window.parent.document.body.style.zoom = "125%";        }    }</script><script type="text/javascript">        function validarFomulario() {                        var msError = '';        var fecha_inicial =  document.getElementById('fecha_ini').value;        var fecha_final =  document.getElementById('fecha_fin').value;                if (fecha_inicial == "") {                                                      msError = "Digita una fecha de Inicio valida";                alert(msError);                document.getElementById('cargando').style.display = 'none';                                $("#fecha_ini").focus();                exit;                               }                if (fecha_final == "") {                               msError = "Digita una fecha final valida";                alert(msError);                document.getElementById('cargando').style.display = 'none';                                $("#fecha_fin").focus();                exit;                         }                    }    </script>