if (window.screen.availWidth <= 640) {            window.parent.document.body.style.zoom = "62%";        }        if (window.screen.availWidth == 800) {            window.parent.document.body.style.zoom = "78%";        }        if (window.screen.availWidth == 1024) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth <= 1280) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth == 1366) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth > 1366) {            window.parent.document.body.style.zoom = "125%";        }    }</script>