 {            if (confirm("Are you sure of deleting this item? ...")){                var action = $(this).attr("href") + "/?item=" + n;                $(this).attr("href",action);            }else            {                return false;            }        }    });</script><script type="text/javascript">    $(window).load(function () {        comprobarScreen();      });</script><script type="text/javascript">    function comprobarScreen()    {        if (window.screen.availWidth <= 640) {            window.parent.document.body.style.zoom = "62%";        }        if (window.screen.availWidth == 800) {            window.parent.document.body.style.zoom = "78%";        }        if (window.screen.availWidth == 1024) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth <= 1280) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth == 1366) {            window.parent.document.body.style.zoom = "100%";        }                if (window.screen.availWidth == 1440) {            window.parent.document.body.style.zoom = "100%";        }                if (window.screen.availWidth == 1600) {            window.parent.document.body.style.zoom = "100%";        }                if (window.screen.availWidth == 1680) {            window.parent.document.body.style.zoom = "100%";        }        if (window.screen.availWidth > 1680) {            window.parent.document.body.style.zoom = "125%";        }    }</script>