ript">        $('#grid tr').click(function() {$(this).find('input[name="item"]').prop('checked', true)   });    $('texto').keypress(function(e){       if (e.keyCode==13)           $('#form1').submit();    })    $('#btn-edit').click(function(e){       item = $('input[name=item]:checked').attr('value');        if (!item){         alert('You must select an Item');         e.preventDefault();       }       else {           var action = $(this).attr("href") + "/" + item;           $(this).attr("href",action);       }   }); $('#btn-delete').click(function(e){        n = $('input[name=item]:checked').attr('value');        if (!n){          alert('You must select an Item');          e.preventDefault();        }else {            if (confirm("Are you sure of deleting this item? ..")){               var action = $(this).attr("href") + "/?item=" + n;               $(this).attr("href",action);            }else			{			return false;			}        }    });</script>