'] as $parque) {?>                    <tr class="row<?php echo $i?>">                        <td><input type="radio" name="sel" id="sel-<?php echo $parque['id']?>" value="<?php echo $parque['id']?>"></td>                        <td><?php echo $parque['nombre']?></td>                        <td><?php echo $parque['stock']?></td>                    </tr>                <?php $i = ($i==0)? 1:0;                } ?>            </tbody>        </table>    </div>    <div  id="dialog_message" style="display:none" title="Add to stock">        <p>            <samp style="font-size:16px; font-family:'Courier New', Courier, monospace; font-weight:bold;" id="txtMensaje">                How many tickets do you want to add?            </samp>        <form id="info" method="POST" >            <label for="ammount"> Ammount </label><input type="number" name="ammout" id="ammount" value="0">            <input type="hidden" id="park" name="park">            <input type="button" value="Add" id="add">        </form>    </div><script>    $(function(){        //events        $("#add-stock").live('click',function(){            if($("input[name='sel']").is(':checked')){            $("#park").val($("input[name='sel']:checked").val());            $("#dialog_message").dialog({                modal:true,                closeText: "hide",                width: 400,                height: 215,                closeOnEsc: false            }).show();            }else{                alert('Please select a park to add tickets');            }        });        $("#add").click(function(evt){            evt.preventDefault();            if(parseInt($("#ammount").val())>0){                $("form").attr('action','<?php echo $data['rootUrl']?>admin/tours/park/add_stock/'+$("#park").val()+'/'+$("#ammount").val());                $("form").submit();            }        });    });</script>